const initState = {

    // jwt:'',
    // username: '',
    // isLogin: false,

};

export default function loginReducer(state = initState, action) {
    switch (action.type) {
        case "LOGIN_SUCCESS":
            const inforName = action.payload;
            return ({
                jwt: inforName. jwt,
                name: inforName.username,
                isLogin: true,
            });


        case "LOGOUT":
            const stateLogOut = {

                jwt: '',
                username: '',
                isLogin: false,
            }
            return stateLogOut;


        default:
            return state;
    }
};
